<?php if (!defined('THINK_PATH')) exit();?><!DOCTYPE html><html lang="en" class="no-js"><head><meta charset="UTF-8" /><meta http-equiv="X-UA-Compatible" content="IE=edge"> <meta name="viewport" content="width=device-width, initial-scale=1"> <title>login</title><link rel="stylesheet" type="text/css" href="/Public/css/normalize.css" /><link rel="stylesheet" type="text/css" href="/Public/css/demo.css" /><!--必要样式--><link rel="stylesheet" type="text/css" href="/Public/css/component.css" /><!--[if IE]><script src="/Public/js/html5.js"></script><![endif]--></head><body>		<div class="container demo-1">			<div class="content">				<div id="large-header" class="large-header">					<canvas id="demo-canvas"></canvas>					<div class="logo_box">						<h3>欢迎登录</h3>						<form  id="forms" name="f" method="post">							<div class="input_outer">								<span class="u_user"></span>								<input name="username" class="text" style="color: #FFFFFF !important" type="text" placeholder="请输入账户">							</div>							<div class="input_outer">								<span class="us_uer"></span>								<input name="password" class="text" style="color: #FFFFFF !important; position:absolute; z-index:100;"value="" type="password" placeholder="请输入密码">							</div>							<div >								<input type="checkbox" name="ischecks" value="1"><font color="#fff">十天内免登录</font>							</div>							<div class="mb2"><input type="button"  onclick="login()" style="	border:0px solid red;line-height: 20px;padding: 15px;margin-top: 20px;display: block;text-align: center;font-size: 18px;border-radius: 50px;width: 330px;background: #0096e6;color:#fff;" value="登录"></div>							<div class="mb2"><a class="act-but submit" href="<?php echo U('add');?>" style="color: #FFFFFF">注册</a></div>						</form>					</div>				</div>			</div>		</div><!-- /container -->		<script src="/Public/js/TweenLite.min.js"></script>		<script src="/Public/js/EasePack.min.js"></script>		<script src="/Public/js/rAF.js"></script>		<script src="/Public/js/jquery.min.js"></script>		<script src="/Public/js/demo-1.js"></script>		<script type="text/javascript" src="/Public/layer/layer.js"></script>		<script type="text/javascript">        function login() {            $.ajax({                //几个参数需要注意一下                type: "POST",//方法类型                dataType: "json",//预期服务器返回的数据类型                url: "<?php echo U('Login/login');?>" ,//url                data: $('#forms').serialize(),                success: function (result) {                   // console.log(result);//打印服务端返回的数据(调试用)                    if (result.status == 1) {                        //信息框-例3//                        layer.msg(result.msg);						alert(result.msg);						window.location.href='http://www.tp3.com/index.php/Home/Index/welcome'                    }else{                        layer.msg(result.msg);					}                },                error : function() {                    layer.msg('网络异常');                }            });        }	</script>	</body></html>